let assert = require("assert");
let GameTable = require("../index.js");
CompareArray = function(array1,array2) {
    return (
        array1.length == array2.length) && array1.every(function(element, index) {
    return element === array2[index]; 
})
};

describe("GameTable", function() {
    describe("#Tables", function() {
        var vArrayTab = [1,2,3,4,5,6,7,8,9];
        var vColNames = ['X','Y','Z'];
        var vRowNames = ['A','B','C'];
        var iR = 3;
        var iC = 3;
        // This part regards loading the data and printing the table

        it("should Automatically determine ColNames and RowNames", function(){
            Table = new GameTable(vArrayTab,iR,iC);
            let x = CompareArray(Table.ColNames,vColNames) && CompareArray(Table.RowNames,vRowNames);
            assert(x);
        });

        it("Should Determine iR if missing", function(){
            Table = new GameTable(vArrayTab,'',iC);
            let x = (Table.Rows == 3);
            assert(x);
        });

        it("Should Determine iC if missing", function(){
            Table = new GameTable(vArrayTab,iR,'');
            let x = (Table.Columns == 3);
            assert(x);
        });

        it("should determine that is a square matrix", function(){
            Table = new GameTable(vArrayTab);
            let x = (Table.Columns == 3) && (Table.Rows == 3);
            assert(x);
        });
    });
});